      return (
      <>
        <div style={{
          width: '100%',
          height: '40px',
          marginTop: '10px',
          marginBottom: '10px',
          backgroundColor: 'red'
        }} >
          {this.props.todos.title}
        </div>
      </>
      );
    }
}
